import UglyThingsContext from '../ContextCreators/UglyThingsContext'



class UglyThingsProvider extends React.Component {
    state = {
        uglyThings: []
    }


    render() {
        return (
            <UglyThingsContext value={{
                    uglyThings: this.state.uglyThings, 
                    addUglyThing: () => null, 
                    removeUglyThing: () => null, 
                    saySomethingAboutAnUglyThing: () => null
                }}>
                    {this.props.children}
            </UglyThingsContext>
        )
    }
}


export default UglyThingsProvider
