import './App.css';
import {UglyThingsContext} from './contextproviders'





class App extends React.Component {


  state = {
    RenderedPosts: []
  }


  componentDidMount() {
    // fetch()
  }

  render() {
    const RenderedPosts = this.state.RenderedPosts.map((item) => {
      return (
        <div>
          <h1>{item.title}</h1>
          <img src={item.url} />
          <h3>{item.description}</h3>
        </div>
      )
    })

    return (
      <UglyThingsContext.UglyThingsConsumer>
        {context => {
          return (
            <div>
              {context.uglyThings}
