import React from 'react';
import './App.css';
import {UglyThingsContext} from './contextproviders'
import { v4 as uuidv4 } from 'uuid';




class App extends React.Component {

  state = {
    RenderedPosts: []
  }


  handleSubmit = () => {
    const uuid = uuidv4()
    console.log(uuid);
  }

  render() {
    const RenderedPosts = this.state.RenderedPosts.map((item) => {
      return (
        <div>
          <h1>{item.title}</h1>
          <img src={item.url} />
          <h3>{item.description}</h3>
        </div>
      )
    })

    return (
      <UglyThingsContext.UglyThingsConsumer>
        {context => {
          const RenderedPosts = context.uglyThings.map((item, index) => {
            return (
              <div key={index}>
                <div>{item.title}</div>
                <div>{item.description}</div>
                <img src={item.url} />
              </div>)
          })
          return (
            <div>
              <form onSubmit={this.handleSubmit}>
                <input name="url" placeholder="Image Url" />
                <br />
                <input name="title" placeholder="Choose Title" />
                <br />
                <input name="describetheugly" placeholder="Why is it ugly?" />


                <input type="submit" onClick={context.removeUglyThing}/>
              </form>

              <div id="ugly-list">
                {RenderedPosts}
              </div>
            </div>
          )
        }}
      </UglyThingsContext.UglyThingsConsumer>
    );
  }
}

export default App;




